
public class nestedforloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int x = 4;

		    // outer loop
		    for (int i = 1; i<=x;++i) {

		      // inner loop to print the numbers
		      for (int j = 1; j <= i; ++j) {
		        System.out.print(j + " ");
		      }
		      System.out.println("");
		    }
		  }
	
}
